package org.cytoscape.io.write;

public interface CyPropertyWriterContext extends CyWriterContext {
	/**
	 * Specifies the property object to be written by the {@link CyWriter} Task
	 * generated by this factory.  This method must be called 
	 * prior to calling the getWriter() method.
	 * @param property The property object to be written. In general 
	 * this object should be of types described in {@link org.cytoscape.property.CyProperty}.
	 */
	void setProperty(Object property);
	
	Object getProperty();
}
