package org.cytoscape.io.write;

import org.cytoscape.session.CySession;

public interface CySessionWriterContext extends CyWriterContext {
	/**
	 * Specifies the {@link org.cytoscape.session.CySession} to be written by the {@link CyWriter} Task
	 * generated by this factory.  This method must be called 
	 * prior to calling the getWriter() method.
	 * @param session The {@link org.cytoscape.session.CySession} to be written.
	 */
	void setSession(CySession session);
	
	CySession getSession();
}
